SELECT SUM("EXPENDITURES"."IS_TRAINING") as agg_0, AVG("EXPENDITURES"."COST") as agg_1, COUNT(*) as agg_2 FROM "EXPENDITURES" LEFT OUTER JOIN "HOUSEHOLDS" ON "EXPENDITURES"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID"  WHERE "EXPENDITURES"."YEAR" != 2017 AND "HOUSEHOLDS"."AGE_REF" != 56 AND "HOUSEHOLDS"."INCOME_RANK_5" >= 0.6465105990837006 AND "HOUSEHOLD_MEMBERS"."MARITAL" BETWEEN 1 AND 4