SELECT MIN("EXPENDITURES"."IS_TRAINING" + "EXPENDITURES"."PRODUCT_CODE") as agg_0, SUM("EXPENDITURES"."YEAR") as agg_1, COUNT(*) as agg_2 FROM "EXPENDITURES" LEFT OUTER JOIN "HOUSEHOLDS" ON "EXPENDITURES"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID"  WHERE "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" BETWEEN 6308678 AND 6675457 AND "HOUSEHOLDS"."INCOME_RANK_4" <= 0.9351353996122903 AND "EXPENDITURES"."GIFT" = 0 AND "HOUSEHOLD_MEMBERS"."WORK_STATUS" IS NOT NULL AND ("HOUSEHOLD_MEMBERS"."MARITAL" = 1 OR "HOUSEHOLD_MEMBERS"."MARITAL" <= 1) AND ("HOUSEHOLDS"."INCOME_RANK_1" <= 0.44650433161040787 OR "HOUSEHOLDS"."INCOME_RANK_1" >= 0.6045033345820567)