SELECT MAX("HOUSEHOLDS"."INCOME_RANK_2") as agg_0, AVG("EXPENDITURES"."HOUSEHOLD_ID" + "HOUSEHOLDS"."INCOME_RANK_5") as agg_1, MIN("EXPENDITURES"."GIFT" + "EXPENDITURES"."PRODUCT_CODE") as agg_2 FROM "EXPENDITURES" LEFT OUTER JOIN "HOUSEHOLDS" ON "EXPENDITURES"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID"  WHERE "EXPENDITURES"."GIFT" BETWEEN 0 AND 1 AND "HOUSEHOLDS"."INCOME_RANK_4" BETWEEN 0.005096527465221797 AND 0.5473829747737999 AND "EXPENDITURES"."IS_TRAINING" BETWEEN 0 AND 1