SELECT AVG("HOUSEHOLDS"."INCOME_RANK_5" + "EXPENDITURES"."PRODUCT_CODE") as agg_0 FROM "HOUSEHOLD_MEMBERS" LEFT OUTER JOIN "HOUSEHOLDS" ON "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID"  WHERE "HOUSEHOLD_MEMBERS"."WORK_STATUS" BETWEEN 1.0 AND 2.0 AND "HOUSEHOLDS"."INCOME_RANK" BETWEEN 0.40653880489434807 AND 0.646491113975068