SELECT SUM("HOUSEHOLD_MEMBERS"."YEAR") as agg_0, MIN("HOUSEHOLD_MEMBERS"."MARITAL" + "HOUSEHOLD_MEMBERS"."AGE") as agg_1, AVG("HOUSEHOLD_MEMBERS"."WORK_STATUS") as agg_2 FROM "HOUSEHOLD_MEMBERS"  WHERE "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" >= 6144732 AND "HOUSEHOLD_MEMBERS"."YEAR" BETWEEN 2015 AND 2017 AND "HOUSEHOLD_MEMBERS"."MARITAL" <= 1 AND "HOUSEHOLD_MEMBERS"."SEX" != 2