SELECT MAX("HOUSEHOLDS"."INCOME_RANK_2") as agg_0, MIN("HOUSEHOLDS"."YEAR") as agg_1 FROM "EXPENDITURES" LEFT OUTER JOIN "HOUSEHOLDS" ON "EXPENDITURES"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID"  WHERE ("HOUSEHOLDS"."AGE_REF" >= 69 OR "HOUSEHOLDS"."AGE_REF" != 15) AND "EXPENDITURES"."COST" <= 4.1043987510991204 AND "HOUSEHOLDS"."INCOME_RANK_4" >= 0.9995281939410781 AND "EXPENDITURES"."EXPENDITURE_ID" <= 977911 AND "HOUSEHOLD_MEMBERS"."MARITAL" = 1