SELECT COUNT(*) as agg_0, SUM("HOUSEHOLDS"."INCOME_RANK_5") as agg_1, SUM("HOUSEHOLDS"."HOUSEHOLD_ID" + "HOUSEHOLD_MEMBERS"."SEX") as agg_2 FROM "HOUSEHOLD_MEMBERS" LEFT OUTER JOIN "HOUSEHOLDS" ON "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID"  WHERE "EXPENDITURES"."PRODUCT_CODE" BETWEEN 188165 AND 253477 OR ("EXPENDITURES"."PRODUCT_CODE" BETWEEN 181708 AND 729722 AND "EXPENDITURES"."PRODUCT_CODE" BETWEEN 111229 AND 181695)