SELECT COUNT(*) as agg_0, MAX("HOUSEHOLDS"."HOUSEHOLD_ID") as agg_1, SUM("HOUSEHOLDS"."AGE_REF" + "HOUSEHOLDS"."INCOME_RANK_2") as agg_2 FROM "EXPENDITURES" LEFT OUTER JOIN "HOUSEHOLDS" ON "EXPENDITURES"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID"  WHERE "HOUSEHOLD_MEMBERS"."WORK_STATUS" BETWEEN 1.0 AND 2.5685216409089904 AND "EXPENDITURES"."MONTH" <= 1 AND "EXPENDITURES"."HOUSEHOLD_ID" >= 6421236 AND "HOUSEHOLDS"."INCOME_RANK_1" BETWEEN 0.009208443530913471 AND 0.026896465338124568