SELECT AVG("EXPENDITURES"."COST" + "EXPENDITURES"."GIFT") as agg_0 FROM "EXPENDITURES"  WHERE "EXPENDITURES"."HOUSEHOLD_ID" >= 4511983 AND ("EXPENDITURES"."MONTH" BETWEEN 1 AND 7 OR "EXPENDITURES"."YEAR" >= 2019 OR "EXPENDITURES"."MONTH" = 9) AND "EXPENDITURES"."EXPENDITURE_ID" <= 106676 AND "EXPENDITURES"."GIFT" != 0