SELECT SUM("EXPENDITURES"."PRODUCT_CODE" + "HOUSEHOLDS"."INCOME_RANK_2") as agg_0, MAX("HOUSEHOLDS"."INCOME_RANK_4") as agg_1, AVG("HOUSEHOLDS"."INCOME_RANK_2" + "HOUSEHOLD_MEMBERS"."WORK_STATUS") as agg_2 FROM "HOUSEHOLD_MEMBERS" LEFT OUTER JOIN "HOUSEHOLDS" ON "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID"  WHERE "EXPENDITURES"."HOUSEHOLD_ID" BETWEEN 4110981 AND 4708720 AND "HOUSEHOLDS"."AGE_REF" != 81 AND "EXPENDITURES"."EXPENDITURE_ID" <= 1632195 AND "HOUSEHOLD_MEMBERS"."WORK_STATUS" <= 1.0