SELECT COUNT(*) as agg_0, COUNT(*) as agg_1, MIN("HOUSEHOLDS"."INCOME_RANK_3" + "HOUSEHOLDS"."AGE_REF") as agg_2 FROM "HOUSEHOLD_MEMBERS" LEFT OUTER JOIN "HOUSEHOLDS" ON "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID"  WHERE "HOUSEHOLD_MEMBERS"."WORK_STATUS" BETWEEN 1.0 AND 1.3628682232298017 AND "HOUSEHOLD_MEMBERS"."AGE" = 74