SELECT MAX("EXPENDITURES"."MONTH" + "HOUSEHOLD_MEMBERS"."WORK_STATUS") as agg_0, SUM("HOUSEHOLD_MEMBERS"."SEX") as agg_1 FROM "EXPENDITURES" LEFT OUTER JOIN "HOUSEHOLDS" ON "EXPENDITURES"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID"  WHERE "HOUSEHOLDS"."INCOME_RANK_5" BETWEEN 0.5011809658197363 AND 0.8022693320858595 AND "HOUSEHOLD_MEMBERS"."SEX" BETWEEN 1 AND 2 AND "HOUSEHOLDS"."YEAR" >= 2017