SELECT AVG("HOUSEHOLD_MEMBERS"."AGE" + "HOUSEHOLD_MEMBERS"."SEX") as agg_0, COUNT(*) as agg_1, MAX("HOUSEHOLD_MEMBERS"."MARITAL" + "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID") as agg_2 FROM "HOUSEHOLD_MEMBERS"  WHERE "HOUSEHOLD_MEMBERS"."SEX" = 2 AND "HOUSEHOLD_MEMBERS"."MARITAL" BETWEEN 1 AND 2 AND "HOUSEHOLD_MEMBERS"."AGE" = 64