SELECT MIN("HOUSEHOLD_MEMBERS"."MARITAL" + "HOUSEHOLD_MEMBERS"."WORK_STATUS") as agg_0, MIN("HOUSEHOLD_MEMBERS"."AGE") as agg_1, COUNT(*) as agg_2 FROM "HOUSEHOLD_MEMBERS"  WHERE "HOUSEHOLD_MEMBERS"."AGE" BETWEEN 34 AND 55 AND "HOUSEHOLD_MEMBERS"."WORK_STATUS" BETWEEN 1.0 AND 1.3059293686385158 AND "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" BETWEEN 4052443 AND 4711273 AND "HOUSEHOLD_MEMBERS"."YEAR" != 2015