SELECT MIN("HOUSEHOLDS"."INCOME_RANK_1" + "HOUSEHOLDS"."INCOME_RANK_3") as agg_0 FROM "EXPENDITURES" LEFT OUTER JOIN "HOUSEHOLDS" ON "EXPENDITURES"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID"  WHERE "HOUSEHOLDS"."INCOME_RANK_1" BETWEEN 0.5563979921551174 AND 0.5714129761467536 AND "HOUSEHOLDS"."INCOME_RANK_MEAN" <= 0.8030635164950736 AND "HOUSEHOLDS"."AGE_REF" >= 28 AND "EXPENDITURES"."COST" >= 1.8055870296617602