SELECT MAX("HOUSEHOLDS"."INCOME_RANK_2" + "HOUSEHOLDS"."INCOME_RANK") as agg_0, AVG("HOUSEHOLDS"."INCOME_RANK" + "HOUSEHOLDS"."AGE_REF") as agg_1, AVG("HOUSEHOLDS"."INCOME_RANK_MEAN" + "HOUSEHOLDS"."YEAR") as agg_2 FROM "HOUSEHOLDS"  WHERE "HOUSEHOLDS"."AGE_REF" != 40 AND "HOUSEHOLDS"."INCOME_RANK" BETWEEN 0.42332641114768693 AND 0.8764127601917933 AND "HOUSEHOLDS"."INCOME_RANK_1" <= 0.7063095120101268 AND "HOUSEHOLDS"."YEAR" >= 2015 AND ("HOUSEHOLDS"."INCOME_RANK_3" >= 0.19576477487342978 OR "HOUSEHOLDS"."HOUSEHOLD_ID" BETWEEN 3149584 AND 3523976 OR "HOUSEHOLDS"."INCOME_RANK_3" <= 0.6493392354769348) AND "HOUSEHOLDS"."INCOME_RANK_2" BETWEEN 0.9165159527717586 AND 0.9921309754982616