SELECT COUNT(*) as agg_0 FROM "EXPENDITURES"  WHERE "EXPENDITURES"."IS_TRAINING" <= 1 AND "EXPENDITURES"."MONTH" BETWEEN 2 AND 8 AND "EXPENDITURES"."YEAR" BETWEEN 2017 AND 2018 AND ("EXPENDITURES"."EXPENDITURE_ID" <= 1479786 OR "EXPENDITURES"."COST" BETWEEN 9.488278149900669 AND 14762.428126725143) AND "EXPENDITURES"."HOUSEHOLD_ID" <= 3138549