SELECT MAX("HOUSEHOLDS"."YEAR") as agg_0, MAX("EXPENDITURES"."GIFT") as agg_1, COUNT(*) as agg_2 FROM "HOUSEHOLDS" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID"  WHERE ("EXPENDITURES"."MONTH" <= 5 OR ("EXPENDITURES"."MONTH" <= 2 AND "EXPENDITURES"."MONTH" = 11)) AND "EXPENDITURES"."HOUSEHOLD_ID" BETWEEN 3319865 AND 3515810 AND "EXPENDITURES"."GIFT" >= 0 AND "HOUSEHOLDS"."INCOME_RANK_2" BETWEEN 0.12920510349700665 AND 0.8515379547300326 AND "EXPENDITURES"."PRODUCT_CODE" >= 184965