SELECT AVG("HOUSEHOLDS"."YEAR" + "HOUSEHOLDS"."INCOME_RANK_3") as agg_0, SUM("HOUSEHOLDS"."INCOME_RANK") as agg_1 FROM "HOUSEHOLD_MEMBERS" LEFT OUTER JOIN "HOUSEHOLDS" ON "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID"  WHERE "HOUSEHOLDS"."INCOME_RANK_4" >= 0.052540764414353076 AND "EXPENDITURES"."HOUSEHOLD_ID" BETWEEN 3994950 AND 5227977 AND "HOUSEHOLD_MEMBERS"."AGE" = 51 AND "EXPENDITURES"."MONTH" = 3 AND "EXPENDITURES"."COST" BETWEEN 0.6320316301002141 AND 7.25871624484078 AND "EXPENDITURES"."PRODUCT_CODE" BETWEEN 162473 AND 447800