SELECT SUM("EXPENDITURES"."PRODUCT_CODE") as agg_0, COUNT(*) as agg_1, SUM("EXPENDITURES"."IS_TRAINING") as agg_2 FROM "HOUSEHOLDS" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID"  WHERE "HOUSEHOLDS"."YEAR" BETWEEN 2015 AND 2016 AND "HOUSEHOLDS"."INCOME_RANK" <= 0.11620672895755321 AND "EXPENDITURES"."PRODUCT_CODE" >= 199868 AND ("HOUSEHOLDS"."AGE_REF" != 57 OR "HOUSEHOLDS"."AGE_REF" = 70) AND "HOUSEHOLD_MEMBERS"."SEX" <= 2