SELECT AVG("HOUSEHOLDS"."INCOME_RANK_1") as agg_0, AVG("HOUSEHOLDS"."INCOME_RANK_MEAN" + "HOUSEHOLDS"."YEAR") as agg_1, MIN("HOUSEHOLD_MEMBERS"."MARITAL" + "HOUSEHOLD_MEMBERS"."YEAR") as agg_2 FROM "EXPENDITURES" LEFT OUTER JOIN "HOUSEHOLDS" ON "EXPENDITURES"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID"  WHERE "EXPENDITURES"."IS_TRAINING" >= 1 AND "EXPENDITURES"."GIFT" BETWEEN 0 AND 1 AND "EXPENDITURES"."PRODUCT_CODE" BETWEEN 137496 AND 183657