SELECT MAX("HOUSEHOLD_MEMBERS"."YEAR" + "HOUSEHOLD_MEMBERS"."WORK_STATUS") as agg_0, MAX("HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" + "HOUSEHOLD_MEMBERS"."SEX") as agg_1 FROM "HOUSEHOLD_MEMBERS"  WHERE "HOUSEHOLD_MEMBERS"."WORK_STATUS" BETWEEN 1.0 AND 2.0 AND "HOUSEHOLD_MEMBERS"."AGE" BETWEEN 13 AND 23 AND ("HOUSEHOLD_MEMBERS"."YEAR" >= 2016 OR ("HOUSEHOLD_MEMBERS"."YEAR" <= 2017 AND "HOUSEHOLD_MEMBERS"."YEAR" = 2019)) AND "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" BETWEEN 3861208 AND 4217325