SELECT MIN("HOUSEHOLDS"."INCOME_RANK_4" + "EXPENDITURES"."YEAR") as agg_0, MAX("EXPENDITURES"."MONTH") as agg_1, MIN("EXPENDITURES"."PRODUCT_CODE") as agg_2 FROM "HOUSEHOLDS" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID"  WHERE "HOUSEHOLDS"."AGE_REF" BETWEEN 29 AND 36 AND ("HOUSEHOLDS"."INCOME_RANK_1" <= 0.10274724650120257 OR "HOUSEHOLDS"."INCOME_RANK_4" BETWEEN 0.11294916480657174 AND 0.7553178560443454)