SELECT AVG("HOUSEHOLDS"."INCOME_RANK" + "EXPENDITURES"."COST") as agg_0, SUM("HOUSEHOLDS"."INCOME_RANK_1" + "HOUSEHOLDS"."HOUSEHOLD_ID") as agg_1, MAX("HOUSEHOLDS"."YEAR") as agg_2 FROM "HOUSEHOLD_MEMBERS" LEFT OUTER JOIN "HOUSEHOLDS" ON "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID"  WHERE "HOUSEHOLDS"."YEAR" <= 2017 AND "EXPENDITURES"."GIFT" <= 0 AND "HOUSEHOLDS"."HOUSEHOLD_ID" >= 4838423 AND "EXPENDITURES"."YEAR" >= 2018 AND "EXPENDITURES"."COST" >= 2.2782810059670076 AND ("HOUSEHOLD_MEMBERS"."AGE" BETWEEN 58 AND 60 OR "HOUSEHOLD_MEMBERS"."AGE" BETWEEN 0 AND 13 OR "HOUSEHOLD_MEMBERS"."AGE" <= 70)