SELECT MIN("EXPENDITURES"."YEAR" + "HOUSEHOLD_MEMBERS"."WORK_STATUS") as agg_0 FROM "HOUSEHOLDS" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID"  WHERE "HOUSEHOLDS"."YEAR" BETWEEN 2016 AND 2018 AND "EXPENDITURES"."HOUSEHOLD_ID" >= 4759239 AND "HOUSEHOLDS"."INCOME_RANK_3" >= 0.47253276167964003