SELECT AVG("EXPENDITURES"."COST" + "HOUSEHOLDS"."INCOME_RANK_2") as agg_0 FROM "EXPENDITURES" LEFT OUTER JOIN "HOUSEHOLDS" ON "EXPENDITURES"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID"  WHERE "EXPENDITURES"."GIFT" = 0 AND "EXPENDITURES"."HOUSEHOLD_ID" <= 4165410 AND ("HOUSEHOLD_MEMBERS"."SEX" <= 2 OR ("HOUSEHOLD_MEMBERS"."WORK_STATUS" BETWEEN 1.0 AND 2.0 AND "HOUSEHOLD_MEMBERS"."SEX" != 1)) AND ("EXPENDITURES"."IS_TRAINING" != 1 OR "EXPENDITURES"."IS_TRAINING" >= 0 OR "EXPENDITURES"."IS_TRAINING" != 1)