SELECT MIN("EXPENDITURES"."EXPENDITURE_ID") as agg_0, MAX("HOUSEHOLDS"."INCOME_RANK_5") as agg_1 FROM "HOUSEHOLDS" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID"  WHERE "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" <= 3335728 AND "EXPENDITURES"."HOUSEHOLD_ID" >= 3438870