SELECT SUM("HOUSEHOLDS"."INCOME_RANK" + "HOUSEHOLDS"."INCOME_RANK_3") as agg_0, AVG("HOUSEHOLDS"."INCOME_RANK_3" + "HOUSEHOLDS"."INCOME_RANK_1") as agg_1 FROM "HOUSEHOLDS" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID"  WHERE "EXPENDITURES"."EXPENDITURE_ID" BETWEEN 606275 AND 1768882 AND "HOUSEHOLDS"."YEAR" != 2016 AND "HOUSEHOLDS"."AGE_REF" BETWEEN 29 AND 51 AND "HOUSEHOLDS"."HOUSEHOLD_ID" BETWEEN 5005141 AND 5098691 AND "EXPENDITURES"."GIFT" = 0 AND ("HOUSEHOLDS"."INCOME_RANK_4" BETWEEN 0.18891228111148783 AND 0.37516343819411796 OR "HOUSEHOLDS"."INCOME_RANK_4" BETWEEN 0.44804952391281017 AND 0.969116405855736 OR "HOUSEHOLDS"."INCOME_RANK_4" >= 0.8505905404030364)