SELECT SUM("HOUSEHOLDS"."INCOME_RANK_MEAN" + "HOUSEHOLDS"."INCOME_RANK") as agg_0, SUM("HOUSEHOLDS"."INCOME_RANK_5") as agg_1 FROM "EXPENDITURES" LEFT OUTER JOIN "HOUSEHOLDS" ON "EXPENDITURES"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID"  WHERE "EXPENDITURES"."YEAR" >= 2017 AND "HOUSEHOLDS"."INCOME_RANK_2" >= 0.17571135169294425