SELECT SUM("HOUSEHOLD_MEMBERS"."AGE" + "HOUSEHOLDS"."AGE_REF") as agg_0 FROM "HOUSEHOLDS" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID"  WHERE "HOUSEHOLD_MEMBERS"."SEX" BETWEEN 2 AND 3 AND "HOUSEHOLDS"."INCOME_RANK_5" <= 0.203520789244729