SELECT AVG("HOUSEHOLD_MEMBERS"."WORK_STATUS" + "HOUSEHOLD_MEMBERS"."AGE") as agg_0, COUNT(*) as agg_1, MAX("HOUSEHOLDS"."INCOME_RANK" + "HOUSEHOLDS"."INCOME_RANK_3") as agg_2 FROM "HOUSEHOLD_MEMBERS" LEFT OUTER JOIN "HOUSEHOLDS" ON "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID"  WHERE "HOUSEHOLDS"."INCOME_RANK" >= 0.3067160926562388