SELECT SUM("EXPENDITURES"."PRODUCT_CODE" + "EXPENDITURES"."MONTH") as agg_0, SUM("HOUSEHOLD_MEMBERS"."MARITAL" + "EXPENDITURES"."YEAR") as agg_1 FROM "EXPENDITURES" LEFT OUTER JOIN "HOUSEHOLDS" ON "EXPENDITURES"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID"  WHERE ("HOUSEHOLD_MEMBERS"."SEX" >= 2 OR "HOUSEHOLD_MEMBERS"."AGE" = 0 OR "HOUSEHOLD_MEMBERS"."SEX" >= 1) AND ("HOUSEHOLD_MEMBERS"."MARITAL" <= 1 OR "HOUSEHOLD_MEMBERS"."MARITAL" BETWEEN 1 AND 2 OR "HOUSEHOLD_MEMBERS"."MARITAL" BETWEEN 1 AND 4) AND "EXPENDITURES"."YEAR" BETWEEN 2017 AND 2018 AND "HOUSEHOLD_MEMBERS"."WORK_STATUS" BETWEEN 1.0 AND 2.0 AND ("HOUSEHOLDS"."INCOME_RANK_1" BETWEEN 0.7963388563712037 AND 0.9594119666445724 OR ("HOUSEHOLDS"."INCOME_RANK_1" >= 0.11348891318377136 AND "HOUSEHOLDS"."INCOME_RANK_1" >= 0.3391581079605735)) AND "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" >= 4580831