SELECT MAX("EXPENDITURES"."YEAR") as agg_0, MAX("HOUSEHOLD_MEMBERS"."WORK_STATUS") as agg_1, SUM("EXPENDITURES"."EXPENDITURE_ID" + "HOUSEHOLDS"."YEAR") as agg_2 FROM "HOUSEHOLDS" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID"  WHERE ("HOUSEHOLD_MEMBERS"."YEAR" = 2019 OR "HOUSEHOLD_MEMBERS"."WORK_STATUS" BETWEEN 1.0 AND 2.0 OR "HOUSEHOLD_MEMBERS"."YEAR" <= 2018) AND "HOUSEHOLDS"."INCOME_RANK_1" <= 0.6037607963767805