SELECT SUM("HOUSEHOLDS"."INCOME_RANK" + "HOUSEHOLDS"."INCOME_RANK_5") as agg_0, SUM("HOUSEHOLDS"."INCOME_RANK_3" + "EXPENDITURES"."YEAR") as agg_1 FROM "EXPENDITURES" LEFT OUTER JOIN "HOUSEHOLDS" ON "EXPENDITURES"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID"  WHERE "HOUSEHOLDS"."AGE_REF" != 16 AND "HOUSEHOLDS"."INCOME_RANK_1" <= 0.13599462875949261 AND "EXPENDITURES"."PRODUCT_CODE" >= 37090 AND "EXPENDITURES"."EXPENDITURE_ID" <= 1236034 AND "HOUSEHOLDS"."HOUSEHOLD_ID" <= 4635263