SELECT COUNT(*) as agg_0, MAX("EXPENDITURES"."PRODUCT_CODE") as agg_1, MAX("EXPENDITURES"."IS_TRAINING") as agg_2 FROM "EXPENDITURES"  WHERE ("EXPENDITURES"."EXPENDITURE_ID" >= 1786921 OR "EXPENDITURES"."PRODUCT_CODE" BETWEEN 111161 AND 159657 OR "EXPENDITURES"."EXPENDITURE_ID" <= 199684) AND "EXPENDITURES"."IS_TRAINING" <= 1 AND "EXPENDITURES"."GIFT" <= 0 AND ("EXPENDITURES"."COST" <= 4.461446253496701 OR ("EXPENDITURES"."COST" BETWEEN 16052.452464101458 AND 41733.07828701735 AND "EXPENDITURES"."COST" >= 1.290814242841359)) AND "EXPENDITURES"."MONTH" <= 3