SELECT MIN("HOUSEHOLD_MEMBERS"."YEAR" + "HOUSEHOLD_MEMBERS"."AGE") as agg_0, SUM("HOUSEHOLD_MEMBERS"."MARITAL") as agg_1, MAX("HOUSEHOLD_MEMBERS"."WORK_STATUS" + "HOUSEHOLDS"."INCOME_RANK_2") as agg_2 FROM "HOUSEHOLDS" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID"  WHERE "HOUSEHOLDS"."YEAR" >= 2016 AND "HOUSEHOLDS"."INCOME_RANK_5" >= 0.07150943931487923 AND "HOUSEHOLDS"."INCOME_RANK_4" >= 0.9872241127577941