SELECT COUNT(*) as agg_0, AVG("HOUSEHOLDS"."INCOME_RANK_1" + "EXPENDITURES"."YEAR") as agg_1, COUNT(*) as agg_2 FROM "HOUSEHOLDS" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID"  WHERE "HOUSEHOLD_MEMBERS"."AGE" = 44 AND "EXPENDITURES"."COST" BETWEEN 3.58806216629976 AND 13392.016061740085 AND "EXPENDITURES"."IS_TRAINING" >= 1 AND "HOUSEHOLDS"."INCOME_RANK_4" <= 0.1571738186502421 AND ("HOUSEHOLDS"."INCOME_RANK_3" >= 0.6428212139347637 OR ("HOUSEHOLDS"."YEAR" BETWEEN 2016 AND 2017 AND "HOUSEHOLDS"."INCOME_RANK_3" <= 0.8831778688688319))