SELECT MAX("HOUSEHOLD_MEMBERS"."MARITAL" + "HOUSEHOLD_MEMBERS"."AGE") as agg_0, MIN("HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" + "HOUSEHOLD_MEMBERS"."SEX") as agg_1, MAX("HOUSEHOLDS"."INCOME_RANK_2") as agg_2 FROM "HOUSEHOLDS" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID"  WHERE "EXPENDITURES"."MONTH" >= 9 AND "EXPENDITURES"."IS_TRAINING" BETWEEN 1 AND 2