SELECT COUNT(*) as agg_0, MAX("EXPENDITURES"."MONTH") as agg_1 FROM "HOUSEHOLD_MEMBERS" LEFT OUTER JOIN "HOUSEHOLDS" ON "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID"  WHERE "HOUSEHOLD_MEMBERS"."MARITAL" >= 5 AND "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" >= 3913136 AND "HOUSEHOLD_MEMBERS"."WORK_STATUS" <= 1.0 AND "HOUSEHOLDS"."INCOME_RANK_3" BETWEEN 0.46939157834515294 AND 0.8977780270973373 AND "EXPENDITURES"."COST" >= 3.8626436338901358 AND "HOUSEHOLD_MEMBERS"."YEAR" <= 2018