SELECT MIN("EXPENDITURES"."GIFT") as agg_0, MIN("HOUSEHOLDS"."HOUSEHOLD_ID" + "EXPENDITURES"."PRODUCT_CODE") as agg_1, SUM("EXPENDITURES"."GIFT" + "EXPENDITURES"."IS_TRAINING") as agg_2 FROM "HOUSEHOLD_MEMBERS" LEFT OUTER JOIN "HOUSEHOLDS" ON "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID"  WHERE "HOUSEHOLD_MEMBERS"."AGE" BETWEEN 63 AND 83 AND "HOUSEHOLD_MEMBERS"."MARITAL" BETWEEN 3 AND 5