SELECT AVG("HOUSEHOLDS"."HOUSEHOLD_ID") as agg_0, AVG("HOUSEHOLD_MEMBERS"."MARITAL") as agg_1, SUM("HOUSEHOLD_MEMBERS"."SEX" + "HOUSEHOLDS"."INCOME_RANK_3") as agg_2 FROM "HOUSEHOLDS" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID"  WHERE "HOUSEHOLD_MEMBERS"."YEAR" >= 2015 AND "EXPENDITURES"."GIFT" != 0 AND "HOUSEHOLDS"."INCOME_RANK_MEAN" >= 0.6968394191949324 AND ("EXPENDITURES"."PRODUCT_CODE" >= 43500 OR "EXPENDITURES"."IS_TRAINING" BETWEEN 1 AND 2 OR "EXPENDITURES"."PRODUCT_CODE" >= 343901) AND "HOUSEHOLDS"."INCOME_RANK_2" >= 0.3025909508546903