SELECT MIN("EXPENDITURES"."YEAR") as agg_0, MAX("HOUSEHOLDS"."INCOME_RANK_1") as agg_1 FROM "HOUSEHOLD_MEMBERS" LEFT OUTER JOIN "HOUSEHOLDS" ON "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID"  WHERE "HOUSEHOLDS"."HOUSEHOLD_ID" BETWEEN 3601873 AND 3971529