SELECT SUM("HOUSEHOLD_MEMBERS"."SEX" + "EXPENDITURES"."YEAR") as agg_0, COUNT(*) as agg_1 FROM "EXPENDITURES" LEFT OUTER JOIN "HOUSEHOLDS" ON "EXPENDITURES"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID"  WHERE "HOUSEHOLD_MEMBERS"."MARITAL" <= 1 AND "EXPENDITURES"."HOUSEHOLD_ID" BETWEEN 4368814 AND 4696488 AND "HOUSEHOLD_MEMBERS"."SEX" BETWEEN 1 AND 2 AND "HOUSEHOLDS"."INCOME_RANK_MEAN" BETWEEN 0.03488654852369713 AND 0.6663632129021052