SELECT MIN("EXPENDITURES"."IS_TRAINING") as agg_0 FROM "EXPENDITURES" LEFT OUTER JOIN "HOUSEHOLDS" ON "EXPENDITURES"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID"  WHERE "HOUSEHOLDS"."YEAR" = 2016 AND "HOUSEHOLD_MEMBERS"."MARITAL" = 1 AND ("HOUSEHOLDS"."INCOME_RANK_MEAN" BETWEEN 0.5352986477874923 AND 0.8600394794836681 OR ("HOUSEHOLDS"."INCOME_RANK_5" >= 0.7007707222124804 AND "HOUSEHOLDS"."INCOME_RANK_MEAN" >= 0.43990266157717617)) AND "HOUSEHOLDS"."HOUSEHOLD_ID" >= 4067011 AND "EXPENDITURES"."YEAR" != 2019