SELECT MIN("EXPENDITURES"."YEAR" + "EXPENDITURES"."COST") as agg_0, COUNT(*) as agg_1, AVG("HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" + "HOUSEHOLDS"."INCOME_RANK") as agg_2 FROM "HOUSEHOLD_MEMBERS" LEFT OUTER JOIN "HOUSEHOLDS" ON "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID"  WHERE "HOUSEHOLD_MEMBERS"."SEX" != 1 AND ("HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" BETWEEN 4506128 AND 4795487 OR ("HOUSEHOLD_MEMBERS"."AGE" = 36 AND "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" BETWEEN 3179258 AND 6270557)) AND "EXPENDITURES"."EXPENDITURE_ID" >= 1994762 AND "HOUSEHOLD_MEMBERS"."MARITAL" <= 1 AND "HOUSEHOLDS"."INCOME_RANK_5" >= 0.3242866401276036 AND "HOUSEHOLDS"."INCOME_RANK_MEAN" >= 0.4682930138222956