SELECT AVG("HOUSEHOLDS"."INCOME_RANK") as agg_0, SUM("HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" + "EXPENDITURES"."COST") as agg_1, MAX("EXPENDITURES"."IS_TRAINING") as agg_2 FROM "EXPENDITURES" LEFT OUTER JOIN "HOUSEHOLDS" ON "EXPENDITURES"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID"  WHERE "HOUSEHOLD_MEMBERS"."AGE" != 14 AND "HOUSEHOLDS"."INCOME_RANK_5" BETWEEN 0.5952074297257481 AND 0.6017725255430186