SELECT AVG("HOUSEHOLD_MEMBERS"."WORK_STATUS") as agg_0 FROM "HOUSEHOLDS" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID"  WHERE ("EXPENDITURES"."YEAR" <= 2016 OR "EXPENDITURES"."COST" >= 1.5367550594756887) AND "HOUSEHOLD_MEMBERS"."AGE" <= 32 AND "HOUSEHOLDS"."YEAR" >= 2017 AND "EXPENDITURES"."MONTH" <= 2 AND "HOUSEHOLD_MEMBERS"."WORK_STATUS" IS NOT NULL