SELECT COUNT(*) as agg_0, AVG("HOUSEHOLD_MEMBERS"."AGE") as agg_1, MAX("HOUSEHOLDS"."INCOME_RANK" + "HOUSEHOLDS"."HOUSEHOLD_ID") as agg_2 FROM "HOUSEHOLD_MEMBERS" LEFT OUTER JOIN "HOUSEHOLDS" ON "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID"  WHERE "EXPENDITURES"."PRODUCT_CODE" <= 478824 AND "EXPENDITURES"."COST" >= 3.137686952407483 AND "HOUSEHOLD_MEMBERS"."WORK_STATUS" <= 1.0