SELECT COUNT(*) as agg_0, MAX("EXPENDITURES"."IS_TRAINING" + "HOUSEHOLDS"."INCOME_RANK_4") as agg_1, MAX("HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" + "HOUSEHOLD_MEMBERS"."AGE") as agg_2 FROM "HOUSEHOLD_MEMBERS" LEFT OUTER JOIN "HOUSEHOLDS" ON "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID"  WHERE "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" <= 4302093 AND "HOUSEHOLD_MEMBERS"."YEAR" <= 2019