SELECT COUNT(*) as agg_0, SUM("HOUSEHOLD_MEMBERS"."YEAR") as agg_1, MIN("HOUSEHOLD_MEMBERS"."YEAR" + "HOUSEHOLDS"."INCOME_RANK_1") as agg_2 FROM "HOUSEHOLDS" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID"  WHERE "HOUSEHOLDS"."INCOME_RANK_3" >= 0.31249532693395077 AND "HOUSEHOLDS"."YEAR" BETWEEN 2016 AND 2018 AND "HOUSEHOLD_MEMBERS"."WORK_STATUS" <= 1.0